class PasswordAnalyzer implements IPasswordAnalyzer {
    
    const COMMON = '';
    const FREQUENCY = 'gL6-A$A:#@\'$KT#<c"06"8*"Wb"5-$Kr t%#f+"S4$7;$ v!R=%!6 g@#s}&\'P#rM"a8"K\'!3= #V }o wx*bZ"X="De#2 #~B!dL!\\\"(u qj#Pf L_"e (1K$9--x6 SE"x- .$)/s%rK(w""QV"oQ ;; {_ [| q@(8t-G7#nU MW cS2$9 UJ GH <]*#S !: >!\'kQ \\Z @A)O8 5& E (L""YZ r_&g2 66 ~& *L!n* zO,|Y)B8 V.#X: qI\'V0 B7 s@4<B)>= I-$wt!;A =h ?G+y: p/ |k"}} &w"]?#72 W) j$ @w S !I700V\'gf (Q cf!9{9cL Qa .i #Z, F *M NZ!0c RX!+%((N S$ ]o#hU#{o Z2#j_ 6P d% I!!wX _Z.nH!i.!@y!6T#a$"Yt ?]!kv Jl"]{ sh!2$&x~"5F.8X AF!n2 :A1G@(\'.%5} 24!TZ XY \\P zq G,).J)>} GD _o Xu*di&m0!%Q  L-KA @I ei%4P <S 8m+NI l& Gg&m+"nS$T{%&? YT e( Mu g2 ~., B(\\P N6 .d .M:^6 gg"]s"k2\':6 LU /h#k_ 6-#$3%p9 W$ 7x&)(%_-">[#Q8 VM y( 3q c* vn/va-K> ^N Il LQ/y9 qF *< /k+^O zp K;"bZ!M\\!>(*NW L\' .-#=<"Uz$^:$o? C] F\' /)"+) FC+8H$M&!l1&2\'#n"&K<!d-#"_ jw A{ J(#~e$Yi#(v.Ng#NF!V8 zM"Q4*RH&.X E""n; ^5 41 gM!xj("Q1![ bn #!";?1la yU mR A6)GK @="w5!k$!bX"b[)K-!R) Mq Fl&uz Zx&p\\ <A X\' f&!B_ xj)GW.3F <G *5 .e-}6 (6 C-!U>)S< :r#=;#:C 9Q!xJ,7< w" Tp#*1%|F#Ze&JW Dn /$ ]("4( H{&>!.@h W[ .F"G}/#k :_ ,U ;$04E \\,!/P)h9!Hq f4( f S& 18 M^":\'#w|#]o 7= !# Y]"*4 YZ-<E0$]"B^ QO bn/$G )> zO e>-|+ \\? SY (d$nj b%)p+%aQ 2P oa!U\\ mp$x: S: *$ &.!9( 3-3KD&WW WG"jB\'`),:t I<\'T_ I=($C Pd!;M 5I 3a"h %_G $, .E (_%wZ(WC!*] w> "$ s} WT h;*Kd UY!JW")u",)"v6!AU"Mv RS"hZ "("Ya&e/%5S-w\\"/W#b1 lF*fp%a6$3c$aH#uf!e> cH!]v 58*En+r< Ic v< gh,&\\ +A ;a$2!)I. !8 :w$>E 2Q fH+LA$)$ Gl+NL"y9"43$30 "8 d% 8l u% cV$pm 6C 2O uH .S K@ $= DB B(!f6 =9 <R *I uH A@ dG )A `K NX 3L !sopL %: o1 h0 9/ (U(5?-Q8!&=!iX"f(0\\[ ~V!ii )\\,<? 2W!l*!}#"\\8#NX)pn S0 Iv!{M$Rs#A2#s"!$R k& ]J!Yi {#4jR%m` --$\\D ;(*>X ]K lS"H~\'c; ;K#hP!/^!(l !S#z+##d [H F\\%Z3/b-#)f /O c% 66!YO 2w+{3,}? {N I1  O3^6 !? xV"s5-3I `R wn (E wj Kq\';: KA XU&_\'#b|$<N$2C qX w# iu!yO Fr($1"T@"pg"eA#+3\'g<!w$":w `7$\\ !Z)#qf%I4%$x*pf!kN"/* T9(Z])+?&O5"9"!nP 2< {9 5q R-\'0~5)4 ^= j8 us9#/ @1 }3 Ul0+- U6 *K!&? +K!]p()0 Z$ va!e} Y< fl!rQ *) C( KJ#f4 $;\'v.5aO W` w*!A<0IC yG ru!KR-*/ <\\ ,-!&3!Ap&Jc\'+` V& s5"gB$gF!`>!B0 hO g. ;w"I5 |v7:@&:o %h#zz H"(wx tL rk!q\\*w9 {D Q-!T_!lF!O$%vF&cB 8 "Mp!SZ\'he!HP rq!>N"BA"Y3 wR8^U$.~ ee#K[";,#IZ ;f!KI!eE!XQ d-#s/$JY%Pz%/F$+Y#2$ XX#Ez\'5g$z9"9$!1y vB 51!La @T*\\f0)j ek L3!^@1t- II Zn m@,08 )0!-@!<-! |"Fp&Oj A& N_ U[ P>!-h$D` 3a!K\' `d#uc%';
    
    protected $_passKey;
    protected $_hash;
    protected $_length;
    protected $_isCommon = false;
    protected $_charsetSize;
    protected $_entropy;
    protected $_strength;
    
    public function __construct($password, $passKey) {
        $this->_passKey = $passKey;
        $this->_hash = Util::passwordHash($password, $passKey);
        $this->_length = mb_strlen($password);
        $this->_isCommon = in_array($password, $this->_getCommonWords());
        $this->_charsetSize = $this->_getCharsetSize($password);
        $this->_entropy = $this->_calculateEntropy($password);
        $this->_strength = $this->_calculateStrength();
    }
    
    public function getHash() {
        return $this->_hash;
    }
    
    public function getPassKey() {
        return $this->_passKey;
    }
    
    public function getLength() {
        return $this->_length;
    }
    
    public function isCommon() {
        return $this->_isCommon;
    }
    
    public function getCharsetSize() {
        return $this->_charsetSize;
    }
    
    public function getEntropy() {
        return $this->_entropy;
    }
    
    public function getStrength() {
        return $this->_strength;
    }
    
    
    
    
    
    protected function _getCommonWords() {
        return explode("0xFF", gzuncompress(base64_decode(self::COMMON)));
    }
    
    protected function _getFrequencyTable() {
        $freqs = [];
        $str = self::FREQUENCY;
        $s = ord(' ');
        $length = strlen($str);
        
        for($pos = 0; $pos < $length;) {
            $c = ord($str{$pos++}) - $s;
            $c /= 95;
            $c += ord($str{$pos++}) - $s;
            $c /= 95;
            $c += ord($str{$pos++}) - $s;
            $c /= 95;
            
            $freqs[] = $c;
        }
        
        return $freqs;
    }
    
    protected function _getFrequencyIndex($char) {
        $char = strtolower($char);
        
        if($char < 'a' || $char > 'z') {
            return 0;
        }
        
        return ord($char) - ord('a') + 1;
    }
    
    protected function _getCharsetSize($password) {
        $output = 0;
        
        if(preg_match('/[a-z]/', $password)) {
            $output += 26;
        }
        
        if(preg_match('/[A-Z]/', $password)) {
            $output += 26;
        }
        
        if(preg_match('/[0-9]/', $password)) {
            $output += 10;
        }
        
        if(preg_match('/[\!\@\#\$\%\^\&\*\(\)]/', $password)) {
            $output += 10;
        }
        
        if(preg_match('/[\`\~\-\_\=\+\[\{\]\}\\\|\;\:\'\"\,\<\.\>\/\?]/', $password)) {
            $output += 20;
        } 
        
        if(preg_match('/ /', $password)) {
            $output += 1;
        }
        
        if(preg_match('/[^ -~]/', $password)) {
            $output += 160;
        }
        
        
        return $output;
    }
    
    protected function _calculateEntropy($password) {
        $lower = Manipulator::factory($password)->toLower();
        $freqTable = $this->_getFrequencyTable();
        
        $charSet = log($this->_getCharsetSize($password)) / log(2);
        $index1 = $this->_getFrequencyIndex($lower[0]);
        $bits = 0;
        
        for($i = 1; $i < $this->_length; $i++) {
            $index2 = $this->_getFrequencyIndex($lower[$i]);
            $c = 1.0 - $freqTable[$index1 * 27 + $index2];
            $bits += $charSet * $c * $c;
            $index1 = $index2;
        }
        
        return $bits;
    }
    
    protected function _calculateStrength() {
        $multiplier = 1 + (0.5 * ($this->_charsetSize / 93));
        
        if($this->_length <= 4) {
            $multiplier *= 0.2; 
        } else if($this->_length <= 8) {
            $multiplier *= 0.5;
        }
        
        if($this->_isCommon) {
            $multiplier *= 0.5;
        }
        
        if($this->_entropy >= 128) {
            $output = 100;
        } else {
            $output = $this->_entropy / 128 * 100;
        }
        
        $output = $output * $multiplier;
        $output = pow($output, 0.5) * 10;
        
        return min($output, 100);
    }
}
